import React, { useEffect, useState } from 'react';
import Overviewstillgoing from './OverviewStillGoing';
import Overviewexpired from './OverviewExpired';
import { withRouter } from "react-router";

const Overview = ({ expiredMachines, stillgoingMachines }) => {
  const [activePumps, setActivePumps] = useState([])

  const sendStopSMS = (pumpnumber, pumpStopcode) => {
    fetch("http://remote.kkpartner.dk:3001/sendsms", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ 
        number: pumpnumber,
        message: pumpStopcode
      })
    })
  }

  const StopMachine = (machine, pump) => {
    fetch("http://remote.kkpartner.dk:3001/stopmachine", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        machineid: machine.id,
        pumpid: pump.id
      })
    })
  }

  useEffect(function() 
  { 
    fetch("http://remote.kkpartner.dk:3001/activepumps")
    .then((data) => data.json())
    .then(function(json) {
      setActivePumps(json)     
    }).catch((error) => {
      console.log(error);
    });
  }, [])

  return (
    <div>
        <h1 className="tablelabel">Aktive vandinger</h1>
        <table className="tables" id="tableoverview">
        <colgroup>
            <col style={{width: "10%"}}></col>
            <col style={{width: "25%"}}></col>
            <col style={{width: "45%"}}></col>
            <col style={{width: "10%"}}></col>
        </colgroup>
        <thead>
            <tr>
            <th>Maskine nr.</th>
            <th>Pumpe Navn</th>
            <th>FÃ¦rdig</th>
            <th>Stop Vanding</th>
            </tr>
        </thead>
        <tbody>
            {stillgoingMachines.map(function(machine)
            {
            var time = new Date(machine["time"]).toLocaleString("da-DK", {
                dateStyle: "medium",
                timeStyle: "short"
            });
            
            var pump = activePumps.find((pum) => pum.name === machine.pumpname)

            return (
                <tr key={machine["id"]}>
                    <td style={{background: "#42CB6B"}}>{machine["id"]}</td>
                    <td>{machine["pumpname"]}</td>
                    <td>{time}</td>
                    <td id="stopwateringbutton" onClick={(event) => {

                        var answer = window.confirm("Hvis du vil stoppe vanding tryk OK")
                        
                        if (!answer === true) return;

                        // var pumpnumber = "+45" + pump.number
                        // var pumpstopcode = pump.stopcode
                        // sendStopSMS(pumpnumber, pumpstopcode)
                        
                        stopMachine(machine.id, pump.id)

                        setTimeout(() => {  window.location.href="/" }, 1000);

                    }}>
                    <h4>STOP</h4>
                    </td>
                </tr>
            )
            })}
        </tbody>
        </table>
    </div>  
  )
}

export default withRouter(Overview);
