import React, { useEffect, useState } from 'react';
import { withRouter } from "react-router";

const Products = ({productList}) => {
  const StopMachine = (machine, pump) => {
    fetch("http://remote.kkpartner.dk:3001/stopmachine", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        machineid: machine.id,
        pumpid: pump.id
      })
    })
  }

  return (
    <div>
        <h1 className="tablelabel">Produkter</h1>
        <table className="tables" id="tableoverview">
        <colgroup>
            <col style={{width: "10%"}}></col>
            <col style={{width: "25%"}}></col>
            <col style={{width: "45%"}}></col>
            <col style={{width: "10%"}}></col>
        </colgroup>
        <thead>
            <tr>
            <th>Produkt ID</th>
            <th>Produkt Navn</th>
            <th>Pris</th>
            <th>Fjern Produkt</th>
            </tr>
        </thead>
        <tbody>
            {productList.map(function(product)
            {
              return (
                <tr key={product["produkt_id"]}>
                    <td style={{background: "#42CB6B"}}>{product["produkt_id"]}</td>
                    <td>{product["navn"]}</td>
                    <td>{product["pris"]}</td>
                    <td id="deleteproductbutton" onClick={(event) => {

                        var answer = window.confirm("Hvis du vil stoppe vanding tryk OK")
                        
                        if (!answer === true) return;

                        setTimeout(() => {  window.location.href="/" }, 1000);

                    }}>
                    <h4>STOP</h4>
                    </td>
                </tr>
              )
              })
            }

        </tbody>
        </table>
    </div>  
  )
}

export default withRouter(Products);
