import React from "react";
import { useState, useEffect } from "react"


const LoginForm = (history) => {
    const [user, setUsername] = useState("")
    const [pass, setPassword] = useState("")
    const [error, setError] = useState("")

    const HandleLogin = (e) => {
        e.preventDefault();

        fetch("http://10.10.60.159:3000/auth", {
            method: "POST",
            headers: {
                "Content-type": "application/json"
            },
            body: JSON.stringify({
                username: user,
                password: pass
            })
        })
        .then(res => res.json())
        .then(json => {
            if (json.error === "wrongdetails") {
                setError("Brugernavn eller Kode er forkert")
            } else if (json.error === "missingdetails") {
                setError("Venligst udfyld begge felter") 
            } else {
                localStorage.setItem('token', json.token);
                localStorage.setItem('fullname', json.fullname);
                history.push("/overview")
            }
        })
    }

    return (
        <main>
            <div className="imgcontainer">
            </div>
            <form onSubmit={HandleLogin} method="post" className="flexCenteredColumn">
                <label>{error}</label> 
                <div className="container">
                    <label><b>Brugernavn123</b>
                        <input type="text" name={user} onChange={(e) => { setUsername(e.target.value) }} required></input>
                    </label>

                    <label><b>Kode</b>
                        <input type="password" name={pass} onChange={(e) => { setPassword(e.target.value) }} required></input>
                    </label>

                    <button type="submit">Log ind</button>          
                </div>
            </form>
        </main>
    )
}

export default LoginForm;
