import React, { useEffect, useState } from 'react';

function Showmachine({shownMachine, setStage}) {

    const HandleClick = () => {
        setStage("maintenance")
    }

    const ConvertTime = (machine) => {
        var time = new Date(machine["time"]).toLocaleString("da-DK", {
            dateStyle: "medium",
            timeStyle: "short"
          });
    }



    return(
        <div>
            <button id="backbutton" onClick={HandleClick}>Tilbage</button>
            <p>{shownMachine.id}</p>
            <p>{shownMachine.pumpname}</p>
            <p>{ConvertTime(shownMachine.time)}</p>
            <p>{shownMachine.active}</p>
        </div>
    )
}
export default Showmachine;
