import { useEffect, useState } from "react"
import { withRouter } from "react-router-dom";

function Employees({setShownEmployee}){

  const [employeesList, setEmployeesList] = useState([])

  const HandleClick = (employee) => {

    setShownEmployee(employee)
    //history.push("/shownemployee")

  }

  useEffect(function() 
  {
    fetch("http://localhost:42069/Employees")
      .then(function(data) {
        return data.json();
      })
      .then(function(json) {
        setEmployeesList(json)     
      }).catch((error) => {
        console.log(error);
      });
  }, [setEmployeesList])

  return(
    <div id="maintenance">
      <h1 id="maintenancetext">Tryk på en boks for at tilgå maskinen</h1>
      {employeesList.map((employee) => {
        var data = employee.active === 1 ? {
          color: "#42CB6B",
          employeename: ["employeeName"],
          phonenumber: ["phoneNumber"]
        } : {
          color: "#DF4848",
          employeename: ["employeeName"],
          phonenumber: ["phoneNumber"]
        }
      return(
        <button key={employee.id} id="maintenanceboxes" onClick={() => {HandleClick(employee)}}>
            <div>
              <h1 id="machineid">
                {employee.id}
              </h1>
              <div id="hid-box" style={{background: data.color}}>
                <p id="active">
                  {data.active}
                </p>
                <div id="maintenanceboxhover">
                  <p className="maintenanceboxheading">
                    Pumpenavn
                  </p>
                  <h3 className="maintenanceboxtext">
                    {data.pumpname}
                  </h3>
                  <p className="maintenanceboxheading">
                    Stop tidspunkt
                  </p>
                  <h3 className="maintenanceboxtext">
                    {data.time}
                  </h3>
                </div>
              </div>
            </div>
        </button>
      )
    })}
    </div>
  )


}

export default withRouter(Employees);
