    var tempMachine = {
      id: id.value,
      active: 0
    } 
  
    fetch("http://10.10.60.161:5000/createmachine", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(tempMachine)
    })
