            <div id="modalbuttonbox">
              <button className="modalbuttons" id="cancelmodalbutton" type="button" onClick={() => {closeModal()}}>Anuller</button>
              <button className="modalbuttons" id="updatemodalbutton" type="submit">Gem</button>
