import React, { useEffect, useState } from 'react';
import Overviewtable from './overviewtable';

function Overview({activeMachines, activePumps, stopMachine, stopPump, setSubmitted}) {

  const [expiredMachines, setExpiredMachines] = useState([])

  function sendStopSMS(pumpnumber, pumpstopcode){
    fetch("http://10.10.60.161:5000/sendsms", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ 
        number: pumpnumber,
        message: pumpstopcode
      })
    })
  }

  useEffect(function() 
  {
    setExpiredMachines(activeMachines.filter(x => new Date() > new Date(x.time)))
  }, [])

  if (expiredMachines) {
    return (
      <div>
      <table className="tables" id="tableoverview">
      <colgroup>
        <col style={{width: "10%"}}></col>
        <col style={{width: "24%"}}></col>
        <col style={{width: "45%"}}></col>
        <col style={{width: "10%"}}></col>
      </colgroup>
      <thead>
        <tr>
          <th>Maskine nr.</th>
          <th>Pumpe Navn</th>
          <th>Færdig</th>
          <th>Stop Vanding</th>
        </tr>
      </thead>
      <tbody>
        {expiredMachines.map(function(machine)
        {
          var time = new Date(machine["time"]).toLocaleString("da-DK", {
            dateStyle: "medium",
            timeStyle: "short"
          });
          
          var pump = activePumps.find((pum) => {
            return pum.name == machine.pumpname
          })

          return (
            <tr key={machine["id"]}>
              <td>{machine["id"]}</td>
              <td>{machine["pumpname"]}</td>
              <td>{time}</td>
              <td id="stopwateringbutton" onClick={(event) => {

                  var answer = window.confirm("Hvis du vil stoppe vanding tryk OK")
                  
                  if (!answer === true) return;

                  //var pumpnumber = "+45" + pump.number
                  //var pumpstopcode = pump.stopcode
                  //sendStopSMS(pumpnumber, pumpstopcode)
                  
                  stopMachine(machine)
                  stopPump(pump)
                  window.location.href="/"
                }}>
              <p id="stopwateringbuttontext">S</p></td>
            </tr>
          )
        })}
      </tbody>
    </table>

    <Overviewtable activeMachines = {activeMachines} activePumps = {activePumps} stopMachine = {stopMachine} stopPump = {stopPump}/>
    
    </div>
    )
  }

  return (
    <Overviewtable activeMachines = {activeMachines} activePumps = {activePumps} stopMachine = {stopMachine} stopPump = {stopPump}/>
  );
}

export default Overview;
