import { useState, useEffect } from "react"

function Maintenance({allMachines, setStage, setShownMachine, setNotes}){

  const HandleClick = (machine) => {

    setShownMachine(machine)
    setStage("showmachine")

  }

  useEffect(function() 
  {
    fetch("http://10.10.60.161:5000/getnotes")
      .then(function(data) {
        return data.json();
      })
      .then(function(json) {
        setNotes(json)     
      }).catch((error) => {
        console.log(error);
      });
  }, [])

  return(
    <div id="maintenance">
      <h1 id="maintenancetext">Tryk på en boks for at tilgå maskinen</h1>
      {allMachines.map((machine) => {
        var active
        if(machine.active == 1) {
          active = "Aktiv"
        }
        else{
          active = "Inaktiv"
        }
      return(
        <div className="maintenanceboxes" onClick={() => {HandleClick(machine)}}>
            {machine.id}
            <p><br></br>{active}</p>
        </div>
      )
    })}
    </div>
  )


}

export default Maintenance;
