import React, { useEffect, useState, useRef } from "react";
import {ThemeProvider} from "styled-components";
import { GlobalStyles } from "./components/globalStyles";
import { lightTheme, darkTheme } from "../Themes";
import {useDarkMode} from "../useDarkMode";

const ProductsList = ({setCurrentProduct, history, createProductInput, setIsMenuOpen}) => {

    const [productList, setProductList] = useState([])

    

    useEffect(() => {        
        let mounted = true;
        fetch("http://localhost:42069/api/Products")
          .then(function(data) {
            return data.json();
          })
          .then(function(json) {
            if (mounted) {
    
              setProductList(json)   
            }  
          }).catch((error) => {
            console.log(error);
          });
      }, [])


    return(
        <React.Fragment>             
            <div className="productstableheaders">
                <h1 className="productstableheaderstext">Produkter</h1>
                <h1 className="productstableheadersplus" onClick={() => {
                    var modal = document.getElementById("productmodal5")
                    modal.style.display = "block"
                    setIsMenuOpen(true)
                    createProductInput.current.focus();
                }}>+</h1>
            </div>
            <table className="productstables" id="tableproducts">
                <colgroup>
                <col style={{width: "10%"}}></col>
                <col style={{width: "50%"}}></col>
                <col style={{width: "15%"}}></col>
                <col style={{width: "10%"}}></col>
                </colgroup>
                <thead>
                <tr>
                    <th>Produktnummer</th>
                    <th>Navn</th>
                    <th>Pris</th>
                    <th></th>
                </tr>
                </thead>
                <tbody>
                {productList.map(function(data) {
                    return (
                        <ThemeProvider theme={themeMode}>
                            <>
                            <GlobalStyles/>
                                <tr key={data["productId"]}>
                                    <td className="td">{data["productId"]}</td>
                                    <td className="td">{data["productName"]}</td>
                                    <td className="td">{data["productPrice"]}</td>
                                    <td className="td" onClick={(event) => {
                                        setCurrentProduct(productList.find(x => x["productId"] === data["productId"]))

                                        var modal = document.getElementById("productmodal2")
                                        console.log(modal)
                                        modal.style.display = "block"
                                        setIsMenuOpen(true)
                                    }}>
                                    <img src="https://icons-for-free.com/iconfiles/png/512/draw+edit+pen+pencil+text+write+icon-1320162307919760358.png" alt="" style={{width: 24}}></img>
                                    </td>
                                </tr>
                            </>
                        </ThemeProvider>
                    )
                })}
                </tbody>
            </table>
      
        </React.Fragment>
    )
}

export default ProductsList;
